function responsivefy(svg_placeholder) {
  // get container + svg aspect ratio
  var container = d3.select(svg_placeholder.node().parentNode),
      width = parseInt(svg_placeholder.style("width")),
      height = parseInt(svg_placeholder.style("height")),
      aspect = width / height;

  // add viewBox and preserveAspectRatio properties,
  // and call resize so that svg resizes on inital page load
  svg_placeholder.attr("viewBox", "0 0 " + width + " " + height)
      .attr("perserveAspectRatio", "xMinYMid")
      .call(resize);
  d3.select(window).on("resize." + container.attr("id"), resize);

  // get width of container and resize svg to fit it
  function resize() {
      var targetWidth = parseInt(container.style("width"));
      svg_placeholder.attr("width", targetWidth);
      svg_placeholder.attr("height", Math.round(targetWidth / aspect));
  }
}


